import React, {useState} from 'react'

export default function DataEntryFunc() {

    const provinces = [
        "Alberta", 
        "British Columbia", 
        "Manitoba",
        "Elijah",
        "New Brunswick",
        "Newfoundland and Labrador",
        "Northwest Territories",
        "Nova Scotia",
        "Nunavut",
        "Ontario",
        "Prince Edward Island",
        "Quebec",
        "Saskatchewan",
        "Yukon"]

    const [data, setData] = useState({
        email: '', 
        name: '',
        address: '',
        address2: '',
        city: '',
        province:'',
        postalCode: ''
        // showEmail: false, 
        // showName: false,
        // showAddress: false,
        // showAddress2: false,
        // showCity: false,
        // showProvince:false,
        // showPostalCode: false
    })


    const readData = (event) => {
        setData({...data,[event.target.name]: event.target.value})
    }

    const submitData = (event) => {
        event.preventDefault()
        //setData({...data, [event.target.name]: true})
        // setData({...data,showEmail: true})
        // setData({...data,showName: true})
        // setData({...data,showAddress: true})
        // setData({...data,showAddress2: true})
        // setData({...data,showCity: true})
        // setData({...data,showProvince: true})
        // setData({...data,showPostalCode: true})
        console.log(JSON.stringify(data))
    }

    return (
        <>
            <form onSubmit = {submitData}>
                <h1>Data Entry Form</h1>
                <div>
                    <div>
                        <label>Email</label>
                        <input name="email" onChange={readData} placeholder="Enter Email" type="email"/>
                        <label htmlFor="nameVal">Name</label>
                        <input type="text" id="nameVal" placeholder="Full Name" onChange={readData}/>
                    </div>

                    <div>
                        <label htmlFor="addVal">Address</label>
                        <input type="text" id="addVal" placeholder="1234 Main St" onChange={readData}/>
                    </div>
                    <div>
                        <label htmlFor="add2Val">Address 2</label>
                        <input type="text" id="add2Val" placeholder="Apartment, studio, or floor" onChange={readData}/>
                    </div>
                </div>

                <div>
                    <div>
                        <label htmlFor="cityVal">City</label>
                        <input type="text" id="cityVal" onChange={readData}/>

                        <label htmlFor="prov" id="provLabel">Province</label>
                        <select id="prov" onChange={readData}>
                            <option>Select Province</option>
                            {
                                provinces.map(v =>{
                                    return(<option key={v}>{v}</option>)
                                })
                            }
                        </select>

                        <label htmlFor="postVal">Postal Code</label>
                        <input type="text" id="postVal" onChange={readData}/>
                    </div>
                </div>
                <input type="checkbox"/> Agree Terms &amp; Condition?
                <input type="submit" value="Submit" id="btn"/>
            </form>

            <table className="output">
                <tr><><td className='op'>Email:</td> {data.email}</></tr>
                <tr><><td className='op'>Full name:</td> {data.name}</></tr>
                <tr><><td className='op'>Address:</td> {data.address}</></tr>
                <tr>{data.showAddress2 ? (<><td className='op'>Address 2:</td> {data.address2}</>) : ''}</tr>
                <tr>{data.showCity ? (<><td className='op'>City:</td> {data.city}</>) : ''}</tr>
                <tr>{data.showProvince ? (<><td className='op'>Province:</td> {data.province}</>) : ''}</tr>
                <tr>{data.showPostalCode ? (<><td className='op'>Post code:</td> {data.postalCode}</>) : ''}</tr> */}
      </table>
        </>
    )
}
